import React from "react";
import { useState } from "react";
import "./MainBody.css";
import "./CardToggle";
import Card from "./Card";
import ProfileImage from "./ProfileImage";
import "./MainNav.css";
import "./CardToggle";
import CardNav from "./CardNav";
import { ReactMarkdown } from "react-markdown/lib/react-markdown";
import remarkGfm from "remark-gfm";

function MainBody(props) {
  const [activeView, setActiveView] = useState("landing");

  const titleContent = `# ` + props.title;

  function toggleMainView(a) {
    setActiveView(a);
    console.log(a);
  }
  console.log(activeView);

  return (
    <div>
      <div className="mainNavContainer">
        <div className="mainNavGrid">
          <div id="bio" className="mainNavLink">
            {
              // eslint-disable-next-line
            }
            <ReactMarkdown># Bio</ReactMarkdown>
            children=`{titleContent}` remarkPlugins={[remarkGfm]}
            />
          </div>
          <CardNav
            title="Skills"
            className="mainNavRowIcon"
            onClick={() => toggleMainView("skills")}
          />
          <CardNav
            title="Timeline"
            className="mainNavRowIcon"
            onClick={() => toggleMainView("timeline")}
          />
          <CardNav
            title="Links"
            className="mainNavRowIcon"
            onClick={() => toggleMainView("links")}
          />
        </div>
      </div>
      <div className="mainContainer">
        <ProfileImage></ProfileImage>
        <div className="mainGrid">
          <Card title="landingContent" contentType={activeView} />
        </div>
      </div>
    </div>
  );
}

export default MainBody;
