function Card(props) {
  const contentLink = "../pages/";

  const handleClick = (e) => {
    e.preventDefault();
    console.log(`${props.title} card was clicked`);
  };

  if (props.contentType === "text") {
    return (
      <div
        className="textCardContainer cardTitle cardHidden"
        onClick={handleClick}
      >
        <h1>{props.title}</h1>
      </div>
    );
  } else if (props.contentType === "image") {
    return (
      <div className="imageCardContainer">
        <img className="cardImage" src={props.image} alt="Profile" />
      </div>
    );
  }
}

export default Card;
