import { ReactMarkdown } from "react-markdown/lib/react-markdown";
import remarkGfm from "remark-gfm";
import "./App.css";
import posts from "../assets/posts.json";

const loadData = () => JSON.parse(JSON.stringify(posts));
const postList = loadData;
const currentPost = postList.id[1].title;
const postContent = currentPost.postContent;

function Post() {
  return <ReactMarkdown children={postContent} remarkPlugins={[remarkGfm]} />;
}

export default Post;
